Advanced.DetectScript:
#!/bin/bash RV=0 reason="No noexec option for /tmp Partition" par=$(grep -v "^#" /etc/fstab|grep "[[:space:]]/tmp[[:space:]]" ) par1=$(mount | grep "[[:space:]]/tmp[[:space:]]") npar=$(grep -v "^#" /etc/fstab|grep "[[:space:]]/tmp[[:space:]]" | grep noexec) mpar=$(mount | grep "[[:space:]]/tmp[[:space:]]" | grep noexec) if [ -n "${par}" ];then
if [ -z "$npar" ]; then RV=1 fi fi if [ -n "${par1}" ];then
if [ -z "$mpar" ]; then RV=1 fi fi if [ $RV -eq 1 ]; then echo $reason >&1 echo "Set noexec option for /tmp Partition." >&2 fi exit $RV
