Advanced.DetectScript:
#!/bin/bash RV=0 reason="No noexec option for /dev/shm Partition" par=$(grep -v "^#" /etc/fstab|grep "[[:space:]]/dev/shm[[:space:]]") par1=$(mount | grep "[[:space:]]/dev/shm[[:space:]]") npar=$(grep -v "^#" /etc/fstab|grep "[[:space:]]/dev/shm[[:space:]]" | grep noexec) mpar=$(mount | grep "[[:space:]]/dev/shm[[:space:]]" | grep noexec) if [ -n "${par}" ];then
if [ -z "$npar" ]; then RV=1 fi fi if [ -n "${par1}" ];then
if [ -z "$mpar" ]; then RV=1 fi fi if [ $RV -eq 1 ]; then echo $reason >&1 echo "Add noexec Option to /dev/shm Partition." >&2 fi exit $RV
