Advanced.DetectScript:
#!/bin/bash RV=0 REASON="No Password for Boot Loader." EXPECT="Set Boot Loader Password."
enabled=$(grep "^set superusers" /boot/grub2/grub.cfg) pass=$(grep "^password" /boot/grub2/grub.cfg)
if [ -z "${pass}" -o -z "${enabled}" ]; then RV=1 fi if [ $RV -eq 1 ]; then echo $REASON >&1 echo $EXPECT >&2 fi exit $RV
