Advanced.DetectScript:
#!/bin/bash RV=0 Reasons="The User/Group Ownership on /etc/gshadow is not root/root." Expected="User/Group Ownership on /etc/gshadow is root/root."
perm=$(stat -c "%U:%G" /etc/gshadow 2>/dev/null) if [ "$perm" != "root:root" ];then RV=1 fi
if [ $RV -eq 1 ]; then echo $Reasons >&1 echo $Expected >&2 fi exit $RV
