Advanced.DetectScript:
#!/bin/bash RV=0 Reasons="Un-owned Files and Directories are found:" Expected="There is no Un-owned Files and Directories."
noUserOrGroup=$(df --local -P | awk {'if (NR!=1) print $6'} | xargs -I '{}' find '{}' -xdev -nouser -print 2>/dev/null) if [ -n "$noUserOrGroup" ];then RV=1 fi
if [ $RV -eq 1 ]; then write=$(echo "$noUserOrGroup" |head -20) echo $Reasons${write} >&1 echo $Expected >&2 fi exit $RV
