Advanced.DetectScript:
#!/bin/bash RV=0 Reasons="There are group or world-writable user configuration files." Expected="No group or world-writable user configuration files."
for dir in `/bin/cat /etc/passwd | /bin/egrep -v '(root|sync|halt|shutdown)' | /bin/awk -F: '($7 != "/sbin/nologin") { print $6 }'`; do for file in $dir/.[A-Za-z0-9]*; do if [ ! -h "$file" -a -f "$file" ]; then fileperm=`/bin/ls -ld $file | /bin/cut -f1 -d" "` if [ `echo $fileperm | /bin/cut -c6 ` != "-" ]; then RV=1 dirs=${dirs}${dirs+","}"${file}" continue fi if [ `echo $fileperm | /bin/cut -c9 ` != "-" ]; then RV=1 dirs=${dirs}${dirs+","}"${file}" continue fi fi done done
if [ -n "${dirs}" ];then RV=1 fi if [ $RV -eq 1 ]; then echo $Reasons${dirs+"Please check the following directory:${dirs}"} >&1 echo $Expected >&2 fi exit $RV
