Advanced.DetectScript:
#!/bin/bash RV=0 REASON="Secure ICMP Redirect Acceptance is enabled." EXPECT=" Disable Secure ICMP Redirect Acceptance."
VALall=$(/sbin/sysctl net.ipv4.conf.all.secure_redirects |cut -d "=" -f2 ) VALdefault=$(/sbin/sysctl net.ipv4.conf.default.secure_redirects |cut -d "=" -f2 )
if [ ${VALall} -ne 0 -o ${VALdefault} -ne 0 ]; then RV=1 fi if [ $RV -eq 1 ]; then echo $REASON >&1 echo $EXPECT >&2 fi exit $RV
